import "./post.css";
import { MoreVert } from "@mui/icons-material";
import axios from "axios";
import {format} from "timeago.js"
import { useState, useEffect, useContext } from "react";
import{Link} from "react-router-dom";
import {AuthContext} from "../../context/AuthContext"
function Post({ post }) {
  const {user:currentuser} = useContext(AuthContext)
  const [like, setLike] = useState(post.likes.length); //2
  const [isLiked, setIsLiked] = useState(false);
  const [user, setUser] = useState({});
  const likeHandler = () => {
    try{
     axios.put(`https://social-backend-vercel.vercel.app/api/posts/${post._id}/like`, {userId:  currentuser._id.$oid})
    }catch(err){
        console.log(err)
    }
    setLike(isLiked ? like - 1 : like + 1);
    setIsLiked(!isLiked);
  };

  useEffect(()=> {
    setIsLiked(post.likes.includes(currentuser._id.$oid))
  },[currentuser._id.$oid, post.likes]);
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(
        `https://social-backend-vercel.vercel.app/api/users?userId=${post.userId}`
      );
      setUser(res.data);
    };
    fetchUser();
  }, [post.userId]);

  return (
    <>
      <div className="post">
        <div className="postWrapper">
          <div className="postTop">
            <div className="postTopLeft">
              <Link to={`/profile/${user.username}`}  style={{ textDecoration: 'none' }}>
              <img
                src={
                  user.profilePicture ||
                  "data:image/jpeg;base64,"
                }
                alt=""
                className="postProfileImg"
              />
              </Link>
              
              <span className="postUserName">{user?.username}</span>
              <span className="postDate">{format(post.createdAt)}</span>
            </div>
            <div className="postTopRight">
              <MoreVert />
            </div>
          </div>
          <div className="postCenter">
            <span className="postText">{post.desc}</span>
            <img src={post?.img} alt="" className="postImg" />
          </div>
          <div className="postBottom">
            <div className="postBottomLeft">
              <img
                className="likeIcon"
                src="/src/assets/like.png"
                onClick={likeHandler}
              ></img>
              <img className="likeIcon" onClick={likeHandler} src="/src/assets/heart.png"></img>
              <span className="postLikeCounter">{like} people like it</span>
            </div>
            <div className="postBottomRight">
              <span className="postCommentCounter">
                {post?.comment === 1
                  ? post?.comment + " Comment"
                  : post?.comment + " Comments"}
              </span>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Post;
